 }
}

if (!IsNumber) {xfa.host.messageBox("Registration Number is in invalid format."); this.rawValue="";xfa.host.setFocus(this.name);}

sfcertform.sfsignaturesf.version.rawValue="2.1";
sfcertform.sfsignaturesf.cltversion.rawValue=xfa.host.version;
sfcertform.sfsignaturesf.numofpages.rawValue=xfa.host.numPages;
